const modalBuyLink=document.querySelectorAll(".button-card-buy"),modalMarkerLink=document.querySelectorAll(".button-card-marker"),buyPopup=document.querySelector(".modal-buy"),modalBuyClose=buyPopup.querySelector(".modal-close"),continueShopping=buyPopup.querySelector(".button-continue-shopping"),basket=document.querySelector(".basket"),marker=document.querySelector(".marker");for(let e=0;e<modalBuyLink.length;e++)modalBuyLink[e].addEventListener("click",function(e){e.preventDefault(),basket.classList.add("add-goods"),buyPopup.classList.add("modal-show")}),modalMarkerLink[e].addEventListener("click",function(e){e.preventDefault(),marker.classList.add("add-goods")});continueShopping.addEventListener("click",function(e){e.preventDefault(),buyPopup.classList.remove("modal-show")}),modalBuyClose.addEventListener("click",function(e){e.preventDefault(),buyPopup.classList.remove("modal-show")}),window.addEventListener("keydown",function(e){"Esc"!==e.key&&"Escape"!==e.key||buyPopup.classList.contains("modal-show")&&(e.preventDefault(),buyPopup.classList.remove("modal-show"))});